package com.kidsmind.cartoon.adapter;import java.util.ArrayList;import java.util.List;import com.kidsmind.cartoon.R;import com.kidsmind.cartoon.uitls.ScreenUtils;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class ResponseAdapter extends BaseAdapter {    private Context mContext;    private List<String> mList;    public ResponseAdapter(Context context, List<String> list) {        mContext = context;        mList = list;    }    @Override    public int getCount() {        return mList.size();    }    @Override    public Object getItem(int position) {        return mList.get(position);    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        TextView tvContent;        if (convertView == null) {            tvContent = new TextView(mContext);            tvContent.setTextColor(mContext.getResources().getColor(R.color.orange_text));            tvContent.setTextSize(ScreenUtils.dpToPx(mContext, 9));            convertView = tvContent;            convertView.setTag(tvContent);        } else {            tvContent = (TextView) convertView.getTag();        }        tvContent.setText((String) getItem(position));        return convertView;    }    public void add(String str) {        mList.add(str);        notifyDataSetChanged();    }    public void add(ArrayList<String> str) {        mList.addAll(str);        notifyDataSetChanged();    }}